Method.Implementation:

	if (data.id = ""){
		set employee = ..%New()
		set employee.LastName = ##class(Sti.Data.Localization.LText).Create(data.lastName) 
      	set employee.FirstName = ##class(Sti.Data.Localization.LText).Create(data.firstName)
      	set employee.MiddleName =##class(Sti.Data.Localization.LText).Create(data.middleName)

    }
    else{
	    set employee = ..%OpenId(data.id)

	    if (employee.LastName){
		set employee.LastName.Value = data.lastName}
		else{
			set employee.LastName = ##class(Sti.Data.Localization.LText).Create(data.lastName)
		}


		if (employee.FirstName){
      	set employee.FirstName.Value = data.firstName}
      	else{
			set employee.FirstName = ##class(Sti.Data.Localization.LText).Create(data.firstName)
		}

      	if (employee.MiddleName){
      	set employee.MiddleName.Value = data.middleName}
      	else{
			set employee.MiddleName =##class(Sti.Data.Localization.LText).Create(data.middleName)
		}

   	}
		set employee.Email = data.email
      	set employee.Phone = data.phone


    	set st = employee.%Save()

		quit employee
