Method.Implementation:
if (data.id = ""){ set internship = ..%New() set internship.Name = ##class(Sti.Data.Localization.LText).Create(data.name,"Sti.Data.Localization.Translation;PersonLTextValue")
} else{ set internship = ..%OpenId(data.id) set internship.Name.Value = data.name } set internship.DateBegin=$ZDATEH(data.dateBegin, 15) set internship.DateEnd=$ZDATEH(data.dateEnd, 15) set internship.MaxPeople=data.maxPeople set internship.Room=data.room set internship.Street= data.street set internship.IsInUse=data.isInUse=$$$YES
set internship.Curator = ##class(Sti.Data.Employee).%OpenId(data.curator.id) set internship.City = ##class(Sti.Data.Region).%OpenId(data.city.id) set internship.Company = ##class(Sti.Data.Company).%OpenId(data.company.id)
set st = internship.%Save()
quit internship
