		do proxy.interns.Insert(..Interns.GetAt(key).ConvertToProxyObject())
	}*/

	set proxy.employees = ##class(%ListOfObjects).%New()
	set key = ""
	for i=1:1:..Contacts.Count()
	{
		set key = ..Contacts.Next(key)
		do proxy.employees.Insert(..Contacts.GetAt(key).ConvertToProxyObject())
	}

    quit proxy

