try{
    // Check privilege for Person table
   // $$$THROWONERROR(st, ##class(Sti.Data.Person).CheckDeletePrivilege())

    set proxy = ##class(%ZEN.proxyObject).%New()
    set proxy.data = ##class(%ZEN.proxyObject).%New()
    set proxy.type = type

    if (type = "settings"){
        set proxy.data.from = ##class(Sti.Data.Settings).GetEmailFrom()
        set proxy.data.password = ##class(Sti.Data.Settings).GetEmailSMTPPassword()
        set proxy.data.port = ##class(Sti.Data.Settings).GetEmailSMTPPort()
        set proxy.data.server = ##class(Sti.Data.Settings).GetEmailSMTPServer()
        set proxy.data.user = ##class(Sti.Data.Settings).GetEmailSMTPUser()
    }
    elseif (type = "operators"){
        set emailList = $LISTFROMSTRING(##class(Sti.Data.Settings).GetMailingOperatorAddresses(), ",")
        set items = ##class(%ListOfObjects).%New()
        for i=1:1:$LISTLENGTH(emailList){
            set item = ##class(%ZEN.proxyObject).%New()
            set item.email = $LIST(emailList, i)
            do items.Insert(item)   
        }

        set proxy.data.items = items
    }
    elseif (type = "students") || (type = "reminder"){
        set proxy.data = ##class(Sti.Utils.Email).GetEmailPattern("orderIntern")
        set proxy.data.days = ##class(Sti.Data.Settings).GetMailingStudentsDays()
    }
    elseif (type = "teacher"){
        set proxy.data = ##class(Sti.Utils.Email).GetEmailPattern(type)
        set proxy.data.days = ##class(Sti.Data.Settings).GetMailingTeacherDays()            
    }
    elseif (type = "curator"){
        set proxy.data = ##class(Sti.Utils.Email).GetEmailPattern("orderCurator")
        set proxy.data.days = ##class(Sti.Data.Settings).GetMailingCuratorDays()
    }
    else{
        set proxy.data = ##class(Sti.Utils.Email).GetEmailPattern(type)
    }

    do proxy.%ToJSON()      
}
catch ex {
    set st = ex.AsStatus()
}

quit st

