      TSTART

      // Check privilege for Person table
//      $$$THROWONERROR(st, ##class(Sti.Data.Person).CheckDeletePrivilege())

      $$$THROWONERROR(st, ##class(%ZEN.Auxiliary.jsonProvider).%ConvertJSONToObject(%request.Content,,.data, 1))

      if (type = "settings"){
          do ##class(Sti.Data.Settings).SetEmailFrom(data.from)
          do ##class(Sti.Data.Settings).SetEmailSMTPPassword(data.password)
          do ##class(Sti.Data.Settings).SetEmailSMTPPort(data.port)
          do ##class(Sti.Data.Settings).SetEmailSMTPServer(data.server)
          do ##class(Sti.Data.Settings).SetEmailSMTPUser(data.user)
      }
      elseif (type = "operators"){
          set emails = ""
          for i=1:1:data.items.Count(){
              set emails = emails_data.items.GetAt(i).email_","
          }

          do ##class(Sti.Data.Settings).SetMailingOperatorAddresses($E(emails, 1, *-1))
      }
      elseif (type = "students") || (type = "reminder"){
          do ##class(Sti.Utils.Email).SetMailPattern("orderIntern", $ZCVT(data.subject, "I", "UTF8"), $ZCVT(data.message, "I", "UTF8"))
          do ##class(Sti.Data.Settings).SetMailingStudentsDays(data.days)
      }
      elseif (type = "feedback"){
          do ##class(Sti.Utils.Email).SetMailPattern(type, $ZCVT(data.subject, "I", "UTF8"), $ZCVT(data.message, "I", "UTF8"))
      }
      elseif (type = "teacher"){
          do ##class(Sti.Utils.Email).SetMailPattern(type, $ZCVT(data.subject, "I", "UTF8"), $ZCVT(data.message, "I", "UTF8"))
          do ##class(Sti.Data.Settings).SetMailingTeacherDays(data.days)          
      }
      elseif (type = "curator"){
          do ##class(Sti.Utils.Email).SetMailPattern(type, $ZCVT(data.subject, "I", "UTF8"), $ZCVT(data.message, "I", "UTF8"))
          do ##class(Sti.Data.Settings).SetMailingCuratorDays(data.days)
      }
      else{
          do ##class(Sti.Utils.Email).SetMailPattern(type, $ZCVT(data.subject, "I", "UTF8"), $ZCVT(data.message, "I", "UTF8"))    
      }


      TCOMMIT 
  }
  catch ex {
      TROLLBACK
      set st = ex.AsStatus()
  }

  quit st

