set status = $$$OK
try{ set list = ##class(%ListOfObjects).%New()
&sql(DECLARE LangCurs CURSOR FOR SELECT Code, Name FROM Sti_Data_Localization.LTextLanguage ORDER BY Code)
&sql(OPEN LangCurs) for{ &sql(FETCH LangCurs INTO :code, :name) quit:(SQLCODE '= 0)
set proxy = ##class(%ZEN.proxyObject).%New() set proxy.code = code set proxy.name = name set proxy.flagUrl = "img/flags/"_code_".png"
do list.Insert(proxy) }
&sql(CLOSE LangCurs)
set proxy = ##class(%ZEN.proxyObject).%New() set proxy.children = list do proxy.%ToJSON() } catch(ex){ set status = ex.AsStatus() }
quit $$$OK

set st = $$$OK
try{ // Преобразовали входную строку JSON в объект proxyObject $$$THROWONERROR(st, ##class(%ZEN.Auxiliary.jsonProvider).%ConvertJSONToObject(%request.Content,,.data, 1))
// Открываем студента по ИД set order = ##class(Sti.Data.Order).%OpenId(data.id,,.st)
// Открыть не удалось - создаем if $$$ISERR(st) set order = ##class(Sti.Data.Order).%New()
set order.LastName = ##class(Sti.Data.Localization.LText).Create($ZCONVERT(data.lastName, "I", "UTF8"), "Sti.Data.Localization.Translation;PersonLTextValue") set order.FirstName = ##class(Sti.Data.Localization.LText).Create($ZCONVERT(data.firstName, "I", "UTF8"), "Sti.Data.Localization.Translation;PersonLTextValue") set order.MiddleName =##class(Sti.Data.Localization.LText).Create($ZCONVERT(data.middleName, "I", "UTF8"), "Sti.Data.Localization.Translation;PersonLTextValue") set order.Email = $ZCONVERT(data.email, "I", "UTF8") set order.Phone = $ZCONVERT(data.phone, "I", "UTF8") set order.Skype = $ZCONVERT(data.skype, "I", "UTF8") set order.Resume = $ZCONVERT(data.resume, "I", "UTF8") set order.MailAgree = data.agree = $$$YES set order.CacheScience = $ZCONVERT(data.cache, "I", "UTF8") set order.EnsembleScience = $ZCONVERT(data.ensemble, "I", "UTF8") set order.DeepSeeScience = $ZCONVERT(data.deepsee, "I", "UTF8") set order.BirthDay=$ZDATEH(data.birthDate, 15)
set order.engMark = ##class(Sti.Data.ENGMark).%OpenId(data.eng.id,,.st) if $$$ISERR(st) set eng = ##class(Sti.Data.ENGMark).%New() set eng = ##class(Sti.Data.ENGMark).%New() set eng = ##class(Sti.Data.ENGMark).%OpenId(data.eng.id,3,.Status) set order.engMark=eng
set order.City = ##class(Sti.Data.Region).%OpenId(data.city.id,,.st) if $$$ISERR(st) set city = ##class(Sti.Data.Region).%New() set city = ##class(Sti.Data.Region).%OpenId(data.city.id,3,.Status) set order.City=city
set order.Internship= ##class(Sti.Data.Internship).%OpenId(data.internship.id) set order.University = ##class(Sti.Data.University).%OpenId(data.university.id)
$$$THROWONERROR(st, order.%Save()) } catch ex { set st = ex.AsStatus() }
quit st

