	$scope.companiesTable.refresh = function(){ 
        $scope.companiesTable.forciblyUpdate++; 
    };

    $scope.companiesTable.add = function(){
        $location.path('/company/'); 
    };

    $scope.companiesTable.edit = function(item){
        $location.path('/company/' + item.id);
    };

    $scope.companiesTable.remove = function(item){       
            CompanySrvc.remove(item.id).then(
                function(data){
                    $scope.companiesTable.selectedItems = [];
                    $scope.companiesTable.refresh();
                    $scope.alert = UtilsSrvc.getAlert('Готово!', 'Университет удален.', 'success', true);
                },
                function(data, status, headers, config){
                    $scope.stalert = {title: 'Внимание!', msg: data, cssClass: 'alert alert-error', visible: true};
                });

    };

    $scope.companiesTable.onSelect = function(item){
        console.log('Selected row ',item);       
    };

    $scope.companiesTable.onSelectCell = function(item, property){
        /*if (!item) return;
        property.onClickCell(item);*/
    };


