Export.CSP:
'use strict';
/*===========================================================================================
Все стажеры
===========================================================================================*/

controllersModule.controller('AllInternCtrl', function($scope, $location, InternSrvc, UtilsSrvc){

$scope.internsTable = {state: {}};

$scope.init = function(){

        $scope.internsTable.columns = [
                          {name: 'Фамилия', sqlName: 'LastName', isSorted: true, isSortable: true, isDown: true, isSearched: true, isSearchable: true},
                          {name: 'Имя', sqlName: 'FirstName', isSorted: false, isSortable: true, isDown: true, isSearched: false, isSearchable: true},
                          {name: 'Отчество', sqlName: 'MiddleName', isSorted: false, isSortable: true, isDown: true, isSearched: false, isSearchable: true},
                          {name: 'Университет', sqlName: 'University', isSorted: false, isSortable: true, isDown: true, isSearched: false, isSearchable: true},
                          {name: 'Город', sqlName: 'City', isSorted: false, isSortable: true, isDown: true, isSearched: false, isSearchable: true}];

        $scope.internsTable.properties = [
        				  	{
	        				  	name: 'lastName',
	        				  	cellStyle: {textAlign: 'left'},
	        				  	cellSelectable: false,
	        				  	cellTitle: 'Привет медвед',
	        					getCssClass: function(item){ 
                                    return '';
                                },
                                onClickCell: function(item){
                                    console.log('О господи, по мне кликнул сам Владимир!', item); 
                                }
	        				},
	        				{
	        				  	name: 'firstName',
	        				  	cellStyle: {textAlign: 'left'},
	        				  	cellSelectable: false,
	        				  	getCssClass: function(item){ 
                                    return '';
                                }
	        				},
	        				{
	        				  	name: 'middleName',
	        				  	cellStyle: {textAlign: 'left'},
	        				  	cellSelectable: false,
	        				  	getCssClass: function(item){ 
                                    return '';
                                }
	        				},
	        				{
	        				  	name: 'university.fullName',
	        				  	cellStyle: {textAlign: 'left'},
	        				  	cellSelectable: false,
	        				  	getCssClass: function(item){ 
                                    return '';
                                }
	        				},
	        				{
	        				  	name: 'city.name',
	        				  	cellStyle: {textAlign: 'left'},
	        				  	cellSelectable: false,
	        				  	getCssClass: function(item){ 
                                    return '';
                                }
	        				}

	        				]; 

        $scope.internsTable.pageSize = 7;
        $scope.internsTable.pageCurr = 1;
        $scope.internsTable.itemsTotal = 0;
        $scope.internsTable.selectedItems = [];
        $scope.internsTable.multiSelectMode = false;
        $scope.internsTable.forciblyUpdate = 0;
        $scope.internsTable.refresh();
    };

    // 
    $scope.internsTable.loadItems = function(pageCurr, pageSize, sqlName, isDown, searchSqlName, searchText){
        InternSrvc.getAllForGrid(pageCurr, pageSize, sqlName, isDown, searchSqlName, searchText, {}).then(
            function(data){
	            data = data.children;
                $scope.internsTable.pageTotal = Math.ceil(data.itemsTotal / pageSize);
                $scope.internsTable.itemsTotal = data.itemsTotal;
                $scope.internsTable.items = data.items;


                if ($scope.internsTable.selectedItems && 
                	$scope.internsTable.items && 
                	$scope.internsTable.selectedItems.length == 0 && 
                	$scope.internsTable.items.length != 0){
                    var selected = $scope.internsTable.items[0];
                    selected.rowClass = 'success';
                    $scope.internsTable.selectedItems = [selected];
                }
            },
            function(data){
                console.log('Ошибка!!!!', data);
            });
    };

	$scope.internsTable.refresh = function(){ 
        $scope.internsTable.forciblyUpdate++; 
    };

    $scope.internsTable.add = function(){
        $location.path('/intern/'); 
    };

    $scope.internsTable.edit = function(item){
        $location.path('/intern/' + item.id);
    };

    $scope.internsTable.remove = function(item){           
            InternSrvc.remove(item.id).then(
                function(data){
                    $scope.internsTable.selectedItems = [];
                    $scope.internsTable.refresh();
                    $scope.alert = UtilsSrvc.getAlert('Готово!', 'Стажер удален.', 'success', true);
                },
                function(data, status, headers, config){
                    $scope.stalert = {title: 'Внимание!', msg: data, cssClass: 'alert alert-error', visible: true};
                });

    };

    $scope.internsTable.onSelect = function(item){
        console.log('Selected row ',item);       
    };

    $scope.internsTable.onSelectCell = function(item, property){
        if (!item) return;
        property.onClickCell(item);
    };


	$scope.init();
});
