Export.CSP:
'use strict';


/*===========================================================================================
Компания. Изменение/Добавление
===========================================================================================*/

controllersModule.controller('CompanyCtrl', function($scope, $routeParams, CompanySrvc){
   $scope.page = {company:{}};

   // Инициализация данных
   $scope.page.init = function(){
      if ($routeParams.id){
          $scope.page.formCaption = "Редактирование компании";
          $scope.page.formBtnSubmitName = "Сохранить";
          $scope.page.loadCompany($routeParams.id);        
      }
      else{
          $scope.page.formCaption = "Добавление компании";
          $scope.page.formBtnSubmitName = "Добавить";


      }
   };
   // Загрузить компанию по ИД
   $scope.page.loadCompany = function(id){
      CompanySrvc.get(id).then(
          function(data){
              $scope.page.company = data;

          },
          function(data, status, headers, config){
              $scope.page.coalert = {title: 'Внимание!', msg: data, cssClass: 'alert alert-error', visible: true};  
          });       
   };
   // Сохранить/создать (отправка формы). Передаем объект на сервер
   $scope.page.submit = function(){
      CompanySrvc.save($scope.page.company).then(
          function(data){
              var msg = 'Изменения сохранены.';

              if (!$routeParams.id){
                  msg = 'Компания добавлена.';
                  $scope.page.company = {};                        
              }

              $scope.page.alert = {title: 'Готово!', msg: msg, cssClass: 'alert alert-success', visible: true, closeTimeout: 1500};
              // Сделать форму "чистой" (блокируется кнопка)
              $scope.pageForm.$setPristine();
          },
          function(data, status, headers, config){
              $scope.page.alert = {title: 'Внимание!', msg: data, cssClass: 'alert alert-error', visible: true};  
          });
   };

   $scope.page.init(); 
});
