Export.CSP:
'use strict';


/*===========================================================================================
Создание стажера из заявки. Изменение/Добавление
===========================================================================================*/

controllersModule.controller('OrderAcceptCtrl', function($scope, $location, $dialog, $window, $routeParams, RegionSrvc, InternSrvc, UniversitySrvc, InternshipSrvc, MarksSrvc,OrderSrvc, $filter, UtilsSrvc){
   $scope.page = {intern:{}};
	$scope.cities = [];
	$scope.idorder='';
	$scope.filter=[];

   // Инициализация данных
   $scope.page.init = function(){
      if ($routeParams.id){
          $scope.page.formCaption = $filter('localize')("Добавление стажера");
          $scope.page.formBtnSubmitName = $filter('localize')("Принять стажера");
          $scope.page.loadOrder($routeParams.id);        
      }
       else if(($routeParams.orId)&&($routeParams.unId))
      {
	      $scope.page.loadOrder($routeParams.orId);
	      //$scope.page.loadUniversity();


	      $scope.page.formCaption = $filter('localize')("Добавление стажера");
          $scope.page.formBtnSubmitName = $filter('localize')("Добавить");
      }
      else{
          $scope.page.formCaption = $filter('localize')("Добавление стажера");
          $scope.page.formBtnSubmitName = $filter('localize')("Добавить");

      }

      $scope.page.loadUniversity();
      $scope.page.loadInternship();
      $scope.page.loadEngMarks();
      $scope.page.loadPetMarks();
   };

   // Загрузить заявку по ИД
  $scope.page.loadOrder = function(id){
      OrderSrvc.get(id).then(

          function(data){
	          $scope.page.intern = data;

	         if(!$scope.page.intern.university)
              {
	            $scope.page.intern.university={id:''} 
	         	if($routeParams.unId)
	         	{
             		$scope.page.intern.university.id=$routeParams.unId;
             		OrderSrvc.save($scope.page.intern);
             		$location.path('/orderaccept/' + $routeParams.orId);

	         	}
              }


             $scope.page.intern.isInNewsletter=$scope.page.intern.isInNewsletter == 1;
             $scope.idorder=id;
          },
          function(data, status, headers, config){
              $scope.page.unalert = UtilsSrvc.getAlert('Внимание!', data, 'error', true);   
          });       
   };

   // Загрузить все универы
  $scope.page.loadUniversity = function(id){
      UniversitySrvc.getAll().then(
          function(data){
              $scope.page.universities = data.children;
          },
          function(data, status, headers, config){
              $scope.page.alert =UtilsSrvc.getAlert('Внимание!', data, 'error', true);   
          });      
   };

   // Загрузить все стажировки
  $scope.page.loadInternship = function(id){
      InternshipSrvc.getAll().then(
          function(data){
              $scope.page.internships = data.children;
          },
          function(data, status, headers, config){
              $scope.page.alert =UtilsSrvc.getAlert('Внимание!', data, 'error', true); 
          });      
   };

  // Загрузить все уровни английского
  $scope.page.loadEngMarks = function(id){
      MarksSrvc.getEngAll().then(
          function(data){
              $scope.page.engmarks = data.children;
          },
          function(data, status, headers, config){
              $scope.page.alert = UtilsSrvc.getAlert('Внимание!', data, 'error', true);   
          });      
   };
    $scope.page.saveIntern= function(msg)
    {
	    $scope.page.intern.id="";
	    InternSrvc.save($scope.page.intern).then(
      	  function(data){

             $scope.page.intern = {};                        
             OrderSrvc.remove($scope.idorder);
			 $scope.page.alert = UtilsSrvc.getAlert('Готово!', msg, 'success', true);
             // Сделать форму "чистой" (блокируется кнопка)
             		$scope.pageForm.$setPristine();
          	 },
          	 function(data, status, headers, config){
              		$scope.page.alert = UtilsSrvc.getAlert('Внимание!', data, 'error', true);   
   			});		 
    };
  // Загрузить все оценки pet
  $scope.page.loadPetMarks = function(id){
      MarksSrvc.getPetAll().then(
          function(data){
              $scope.page.petmarks = data.children;
          },
          function(data, status, headers, config){
              $scope.page.alert = UtilsSrvc.getAlert('Внимание!', data, 'error', true);   
          });      
   };

   // Сохранить/создать (отправка формы). Передаем объект на сервер
   $scope.page.submit = function(){
   InternSrvc.findSimmilarIntern($scope.page.intern.lastName,$scope.page.intern.firstName,$scope.page.intern.birthDate).then(
	  function(data){
		  if(data.status!=0)
		  {
			  		var msg = 'Стажёр с таким именем/фамилией/датой рождения есть! Изменить старую запись или добавить нового стажёра?';
				    var btns= [{result: '0', label: $filter('localize')('Добавить'),  cssClass: 'btn-small', func:null},{result: '1', label: $filter('localize')('Изменить'),  cssClass: 'btn-small', func:null}];
	    		 	$dialog.messageBox($filter('localize')('Внимание!'), $filter('localize')(msg), btns).open().then(function(result){
		    		 if(result==1)	
		    		 {
		    		  	 $scope.page.intern.id = data.result.id; 
		    		  	 $scope.page.saveIntern('Изменения сохранены.');
		    		 }
		    		 else if(result==0)
		    		 	$scope.page.saveIntern('Стажёр добавлен.');
		    	    })  	    
		  }
		  else
		   $scope.page.saveIntern('Стажёр добавлен.');

	  },
   	  function(data, status, headers, config){
              $scope.page.alert = UtilsSrvc.getAlert('Внимание!!', data, 'error', true);   
      });  
 	};

    // Загрузить города
    $scope.loadCities = function(startsWith){       
        if(!startsWith || startsWith.length == 0)
            $scope.cities = [];

        if (!startsWith || startsWith.length != 2)
            return;  

        RegionSrvc.getAllCitiesStartsWith(startsWith).then(
            function(data){
                $scope.cities = data.children;
            },
            function(response){
                $scope.alert = UtilsSrvc.getAlert('Ошибка !', response.data, 'error', true);
            }); 
    };

     /* Проверка данных на корректность */
    $scope.page.formatData = function(){
	    $scope.page.intern.birthDate = UtilsSrvc.getValidDate($scope.page.intern.birthDate);
        if ($scope.page.intern.birthDate == ""){
        	$scope.page.intern.birthDate = "";}
    };


    /// Отмена - возврат на предыдущую страницу
    $scope.page.cancel = function(){
        $window.history.back();
    };


   $scope.page.init(); 
});
