Export.CSP:
'use strict';

/*===========================================================================================
Университет. Изменение/Добавление
===========================================================================================*/

controllersModule.controller('UniversityCtrl', function($scope, $routeParams, RegionSrvc, UniversitySrvc ){
   $scope.page = {university:{}};
   $scope.cities = [];

   // Инициализация данных
   $scope.page.init = function(){
      if ($routeParams.id){
          $scope.page.formCaption = "Редактирование университета";
          $scope.page.formBtnSubmitName = "Сохранить";
          $scope.page.loadUniversity($routeParams.id);        
      }
      else{
          $scope.page.formCaption = "Добавление университета";
          $scope.page.formBtnSubmitName = "Добавить";


      }
   };
   // Загрузить стажера по ИД
   $scope.page.loadUniversity = function(id){
      UniversitySrvc.get(id).then(
          function(data){
              $scope.page.university = data;

          },
          function(data, status, headers, config){
              $scope.page.unalert = {title: 'Внимание!', msg: data, cssClass: 'alert alert-error', visible: true};  
          });       
   };

   // Сохранить/создать (отправка формы). Передаем объект на сервер
   $scope.page.submit = function(){
      UniversitySrvc.save($scope.page.university).then(
          function(data){
              var msg = 'Изменения сохранены.';

              if (!$routeParams.id){
                  msg = 'Университет добавлен.';
                  $scope.page.university = {};                        
              }

              $scope.page.alert = {title: 'Готово!', msg: msg, cssClass: 'alert alert-success', visible: true, closeTimeout: 1500};
              // Сделать форму "чистой" (блокируется кнопка)
              $scope.pageForm.$setPristine();
          },
          function(data, status, headers, config){
              $scope.page.alert = {title: 'Внимание!', msg: data, cssClass: 'alert alert-error', visible: true};  
          });
   };

   // Загрузить города
   $scope.loadCities = function(startsWith){       
        if(!startsWith || startsWith.length == 0)
            $scope.cities = [];

        if (!startsWith || startsWith.length != 2)
            return;  

        RegionSrvc.getAllCitiesStartsWith(startsWith).then(
            function(data){
                $scope.cities = data.children;
            },
            function(response){
                $scope.alert = UtilsSrvc.getAlert('Ошибка !', response.data, 'error', true);
            }); 
    };

   $scope.page.init(); 
});
