Export.CSP:
'use strict';
/*===========================================================================================
Стажер
===========================================================================================*/

servicesModule.factory('InternSrvc', function(RESTSrvc) {    
    return {
        /* Сохранить / создать стажера */
        save: function(intern){
            return RESTSrvc.getPromise({method: 'POST', url: StiSetting.appName + '/intern', data: intern});
        },
        /* Удалить стажера по ИД */
        remove: function(id){
            return RESTSrvc.getPromise({method: 'DELETE', url: StiSetting.appName + '/intern/' + id});
        },
        /* Получить стажера по ИД */
        get: function(id){
            return RESTSrvc.getPromise({method: 'GET', url: StiSetting.appName + '/intern/' + id});
        },
        /* Получить всех стажеров */
        getAll: function(){
            return RESTSrvc.getPromise({method: 'GET', url:StiSetting.appName+'/intern'});
        }
    }
});
