Export.CSP:
'use strict';
/*===========================================================================================
Университет
===========================================================================================*/

servicesModule.factory('UniversitySrvc', function(RESTSrvc) {    
    return {
        /* Сохранить / создать универ */
        save: function(university){
            return RESTSrvc.getPromise({method: 'POST', url: StiSetting.appName + '/university', data: university});
        },
        /* Удалить универ по ИД */
        remove: function(id){
            return RESTSrvc.getPromise({method: 'DELETE', url: StiSetting.appName + '/university/' + id});
        },
        /* Получить универ по ИД */
        get: function(id){
            return RESTSrvc.getPromise({method: 'GET', url: StiSetting.appName + '/university/' + id});
        },
        /* Получить все универы */
        getAll: function(){
            return RESTSrvc.getPromise({method: 'GET', url: StiSetting.appName + '/university'});
        },
        /* Получить всех стажеров универа */
        getInterns: function(id){
            return RESTSrvc.getPromise({method: 'GET', url: StiSetting.appName + '/university/' + id + '/intern'});
        }
    }
});
